# Setting the width and caption of the window
WINDOW = pygame.display.set_mode((WIDTH,WIDTH))
pygame.display.set_caption("Evacuation Route Path Finding")

# Making the grid of the map
def make_grid(rows, width):
    grid = []
    gap = width // rows
    for i in range(rows):
        grid.append([])
        for j in range(rows):
                node = Node(i, j, gap, rows)
                grid[i].append(node)
    
    return grid

# Draw the grid on the screen
def draw_grid(window, rows, width):
    gap = width // rows
    for i in range(rows):
        pygame.draw.line(window, GREY, (0, i * gap), (width, i * gap))
        for j in range(rows):
            pygame.draw.line(window, GREY, (j * gap, 0), (j * gap, width))

# Function to draw nodes
def draw(window, grid, rows, width):
    window.fill(WHITE)

    # Color each node
    for row in grid:
        for node in row:
            node.draw(window)

    # Draw the grid over the screen
    draw_grid(window, rows, width)
    pygame.display.update()

# Get row and column of the position of the mouse when its clicked
def get_clicked_position(position, rows, width):
    gap = width // rows
    y, x = position
    row = y // gap
    col = x // gap
    return row, col

# Function to spawn a random obstacle
def spawn_random_obstacle(grid, rows, start, end, survivor):
    row = random.randint(0, rows - 1)
    col = random.randint(0, rows - 1)
    node = grid[row][col]

    # Ensure the obstacle doesn't replace start, end, or survivor positions
    if node != start and node != end and (not survivor or node != survivor.current_spot) and not node.is_barrier():
        node.make_barrier()
        return node
    return None

# Function to spawn pre defined obstacle
def spawn_predefined_obstacles(grid, predefined_obstacles, start, end, survivor):
    for row, col in predefined_obstacles:
        node = grid[row][col]
        
        # Ensure the obstacle doesn't replace start, end, or survivor positions
        if node != start and node != end and (not survivor or node != survivor.current_spot) and not node.is_barrier():
            node.make_barrier()
            return node
    return None

# Function to save the grid to a file with incremented filenames
def save_grid(grid):

    # Check for existing files and determine the next save index
    for i in range(1, 10):
        filename = f"saved_grid_{i}.pkl"
        try:
            with open(filename, "rb"):
                continue
        except FileNotFoundError:
            with open(filename, "wb") as f:
                pickle.dump(grid, f)
            print(f"Grid saved as {filename}.")
            return
    print("All save slots are full. Please delete a saved file to save a new grid.")


# Function to load the grid from a file
def load_grid(index):
    filename = f"saved_grid_{index}.pkl"
    try:
        with open(filename, "rb") as f:
            grid = pickle.load(f)
        print(f"Grid loaded from {filename}.")
        return grid
    except FileNotFoundError:
        print(f"Save file {filename} not found.")
        return None

