# Main function
def main(window, width):
    ROWS = 50
    grid = make_grid(ROWS, width)

    global next_node
    next_node = None
    start = None
    end = None
    original_start = None
    new_obstacles = []
    path = []
    dup_path = []

    survivor = None
    survivors = 0

    simulation_started = False
    grid_changed = False
    no_path_exist = False

    run = True

    while run:
        draw(window, grid, ROWS, width)

        # Survivor movement logic
        if survivor:
            if survivor.current_spot.is_barrier():
                print("Survivor died due to obstacle.")
                survivor = None  # Remove survivor if they are on a barrier
            elif path:  # Continue path traversal
                if next_node is None:
                    next_node = path.pop(0)
                if next_node.get_color() != BLACK:
                    next_node = path.pop(0)
                    survivor.move_to(next_node)
                survivor.draw(window)
                pygame.display.update()
                pygame.time.delay(30)
            elif not path and not no_path_exist:  # Path traversal complete
                if start == original_start:
                    grid_changed = False
                else:
                    grid_changed = True
                survivors += 1
                survivor.move_to(original_start)
                start = original_start
                path = dup_path[:]
        elif not survivor and simulation_started and path:
            survivor = Survivor(original_start)
            path = dup_path[:]
            next_node = None
        
        # If an obstacle is blocking the pathway
        if new_obstacles is not None and path:
             for obstacle in new_obstacles:
                if obstacle in path:
                    grid_changed = True 
                    break

        for event in pygame.event.get():
            if event.type == pygame.QUIT:
                run = False
                print(survivors)

            # Mouse input handling for map creation
            if pygame.mouse.get_pressed()[0]: # Adding nodes
                pos = pygame.mouse.get_pos()
                row, col = get_clicked_position(pos, ROWS, width)
                node = grid[row][col]
                if not start and node != end:
                    start = node
                    node.make_start()
                elif not end and node != start:
                    end = node
                    end.make_end()
                elif node != end and node != start:
                    node.make_barrier()
                    if node in dup_path:
                        new_obstacles.append(node)

            elif pygame.mouse.get_pressed()[2]: # Removing nodes
                pos = pygame.mouse.get_pos()
                row, col = get_clicked_position(pos, ROWS, width)
                node = grid[row][col]
                if node in new_obstacles:
                    new_obstacles.remove(node)
                node.reset()
                if node == start:
                    start = None
                if node == end:
                    end = None
                grid_changed = True

            # Keyboard input handling
            if event.type == pygame.KEYDOWN:

                # Starting the algorithm
                if event.key == pygame.K_SPACE and start and end:
                    grid_changed = True  
                    simulation_started = True
                    next_node = None
                    survivor = None

                # Clearing all nodes
                if event.key == pygame.K_c: 
                    start = None
                    end = None
                    next_node = None
                    survivor = None
                    grid = make_grid(ROWS, width)
                    grid_changed = False
                    simulation_started = False
                    original_start = None

                # Saving map
                if event.key == pygame.K_p:  
                    save_grid(grid)

                # Loading maps using keys 1-9
                if pygame.K_1 <= event.key <= pygame.K_9:
                    grid_index = event.key - pygame.K_0
                    loaded_grid = load_grid(grid_index)
                    if loaded_grid:
                        grid = loaded_grid
                        for row in grid:
                            for node in row:
                                if node.get_color() == ORANGE or node.get_color() == TURQUOISE:
                                    node.reset()
                        start = None
                        end = None
                        survivor = None
                        next_node = None
                        path = []
                        dup_path = []
                        simulation_started = False
                        original_start = None
        
        # Recalculate path if grid changes
        if simulation_started and start and end and grid_changed:
            if original_start == None:
                original_start = start

            for row in grid:
                for node in row:
                    node.update_neighbors(grid)

            start_time = time.time()
            tracemalloc.start()

            # Change algorithm function to either dijkstra_algorithm or a_star_algorithm
            path = dijkstra_algorithm(lambda: draw(window, grid, ROWS, width), grid, start, end)
            if path is None:
                no_path_exist = True
                print(str(survivors) + "survivors escaped")
            else:
                no_path_exist = False

            current_memory, peak_memory = tracemalloc.get_traced_memory()
            tracemalloc.stop()
            end_time = time.time()

            execution_time = end_time - start_time
            print(f"Execution Time: {execution_time:.4f} seconds")
            print(f"Current Memory Usage: {current_memory / 1024:.2f} KB")
            print(f"Peak Memory Usage: {peak_memory / 1024:.2f} KB")

            if path:
                survivor = Survivor(start)
                dup_path = path[:]
            grid_changed = False  # Reset the flag

        # Randomly spawn obstacles
        if simulation_started and random.random() < 1 and not no_path_exist:
            new_obstacles.append(spawn_predefined_obstacles(grid, predefined_obstacles, start, end, survivor))
       
    pygame.quit()


main(WINDOW, WIDTH)